mapper.select:
select empi,name,sex,birthday,address,visitType,inHospitalTime,outHospitalTime,visitCorp,visitOrg,visitOrgName,diagnosis,hospitalNum from ( select distinct p.personid as empi, p.xm as name,p.xb as sex,p.csrq as birthday, p.gzdwdz as address, '2' as visitType, zy.rysj as inHospitalTime, zy.cysj as outHospitalTime, zy.yljgdm as visitCorp, zy.jzksbm as visitOrg,zy.jzksmc as visitOrgName, zy.zdmc as diagnosis, zy.cisid as hospitalNum from TB_YL_Patient_Information p left join TB_YL_ZY_Medical_Record zy on p.personid = zy.personid where zy.rysj is not null
and (p.xm like concat(concat('%',#{queryKeywords}),'%') or zy.cisid like concat(concat('%',#{queryKeywords}),'%'))
and zy.rysj between #{inHospitalStartTime} and #{inHospitalEndTime}
and zy.jzksbm IN
#{dept}
union all select distinct p.personid as empi, p.xm as name ,p.xb as sex,p.csrq as birthday, p.gzdwdz as address, '1' as visitType, mz.jzksrq as inHospitalTime,'' as outHospitalTime , mz.yljgdm as visitCorp, mz.jzksbm as visitOrg,mz.jzksmc as visitOrgName, mz.mzzdmc as diagnosis, mz.jzlsh as hospitalNum from TB_YL_Patient_Information p left join TB_YL_MZ_Medical_Record mz on p.personid = mz.personid where mz.jzksrq is not null
and (p.xm like concat(concat('%',#{queryKeywords}),'%') or mz.jzlsh like concat(concat('%',#{queryKeywords}),'%'))
and to_date(mz.jzksrq, 'YYYYMMDD') between #{inHospitalStartTime} and #{inHospitalEndTime}
and mz.jzksbm IN
#{dept}
) foo
where visitType in
#{visitType}
order by empi
